export default function Home({navigation}) {
    const user = "Daniel";
    const saldo = 0.00;

    const renderItem = ({ item }) => (
        <View style={styles.item}>
            <View style={styles.data}>
                <Text style={styles.name}>{item.name}</Text>
                <Text style={styles.email}>{item.email}</Text>
            </View>

            <View style={styles.itemButtons}>
                <TouchableOpacity>
                    <FontAwesome5 name="pencil-alt" size={24} color="black" />
                </TouchableOpacity>

                <TouchableOpacity>
                    <AntDesign name="delete" size={24} color="black" />
                </TouchableOpacity>
            </View>
        </View>
    );

 return (
    <View style={styles.view}>
        <View>
            <Text style={{color:'green', fontSize:32, fontFamily:'bold', marginLeft:10}}>{user}</Text>
            <Text style={{color:'red',fontSize:30, marginLeft:12}}>Saldo: {saldo}</Text>
        </View>
        <View style={styles.head}>
            <Text style={styles.header}>Minhas Movimentações</Text>  
        </View>

        <FlatList
            data={data}
            renderItem={renderItem}
            keyExtractor={item => item.id}
            showsVerticalScrollIndicator={false}
            contentContainerStyle={{paddingBottom: 50}}
            ListEmptyComponent={() => (
                <Text style={{textAlign: 'center'}}>
                    Nenhum usuário cadastrado!
                </Text>
            )}
        />

        <TouchableOpacity style={styles.addUser} onPress={() => navigation.navigate('Register')}>
            <Text style={styles.txtAddUser}>+</Text>
        </TouchableOpacity>
    </View>
  );
}

