import React, { useEffect, useState } from "react";
import {useNavigation} from "@react-navigation/native";
import { View,Text,Image,StyleSheet } from "react-native";
import {getProduct} from "../../components/banco";
import { getDocs, collection, deleteDoc, doc } from 'firebase/firestore';





export default function Detalhes ({route}){

    //const [data,setData] = useState({})
    const id = route.params.item;
    const desc = route.params.desc;
    const preco = route.params.preco;
    const titulo = route.params.titulo;
    const img = route.params.img;

    
    
    return(
        
        <View>
            <Text>Produto</Text>
            <Image
                style={styles.image}
                    source={{
                         uri: img
                    }}
            />
            <Text>{titulo}</Text>
            <Text>descricao: {desc}</Text>
            <Text>Pre√ßo: {preco}</Text>
            
        </View>

    );
}
const styles = StyleSheet.create({
    view: {
        flex: 1,
        paddingTop: 50,
        paddingHorizontal: 25,
        backgroundColor: '#000',
        width: '100%',
        height:'100%',
    },
    image: {
        width: 100,
        height: 100,
    },
    head: {
        flexDirection: 'row',
        alignItems: 'center',
        justifyContent: 'center',
        color: '#FFF',
        fontWeight: 'bold',
        marginBottom: 25
    },
    header:{
        color: '#FFF',
        fontSize: 32,
        fontWeight:'bold'
    },
    item: {
        height: 150,
        backgroundColor: '#FFF',
        elevation: 1,
        marginVertical: 5,
        marginHorizontal:5,
        borderRadius: 10,
        padding: 15,
        flexDirection: 'row',
        alignItems: 'center'
    },
    data: {
        flex: 1,
    },
    itemButtons: {
        paddingHorizontal: 15,
        justifyContent:'space-between',
        flex: 1,
    },
    id: {
        fontSize: 14,
    },
    name: {
        fontSize: 18,
        fontWeight: 'bold',
        color: 'black'
    },
    email: { 
        color: '#000',
        fontSize: 16,
    },
    addUser: {
        position: 'absolute',
        right: 15,
        bottom: 15,
        elevation: 10,
        backgroundColor: 'green',
        borderRadius: 50,
        height: 70,
        width: 70,
        justifyContent: 'center',
        alignItems: 'center'
    },
    addUser2: {
        position: 'relative',
        right: 15,
        bottom: 15,
        elevation: 10,
        backgroundColor: 'red',
        borderRadius: 50,
        height: 70,
        width: 70,
        justifyContent: 'center',
        alignItems: 'center'
    },
    txtAddUser: {
        fontSize: 32,
        fontWeight:'bold',
        color: '#FFF'
    }
})
