            valor: parseInvalor,
            desc: desc
          }).then(()=>{
            alert("Valor adicionado")

          }).catch((error) => {
            alert("Erro "+error);
          })
          }

 return (
    <View style={styles.view}>
        <Image 
            source={require('../../images/support.png')} 
            style={styles.image} 
        />
        <Text style={styles.header}>Cadastro de movimentação</Text>

        <TextInput 
            value={desc}
            onChangeText={setDesc}
            style={styles.input}
            placeholder="um titulo..."
            placeholderTextColor="#FFF"
        />

        <TextInput 
            value={Valor}
            onChangeText={setValor}
            style={styles.input}
            placeholder="valor.."
            placeholderTextColor="#FFF"
        />
        
        <TouchableOpacity onPress={create}
            style={styles.button}
        >
            <Text style={styles.txtButton}>Adicionar</Text>
        </TouchableOpacity>

    </View>
  );
}

const styles = StyleSheet.create({
    view: {
        flex: 1,
        paddingHorizontal: 25,
        backgroundColor: '#fff',
        width: '100%',
        justifyContent: 'center',
        alignItems: 'center'
    },
    image: {
        width: 250,
        height: 250,
        marginBottom: 15,
    },
    header: {
        fontSize: 34,
        color: 'black',
        fontWeight: 'bold',
        marginBottom:10,
    },
    input: {
        width: '90%',
        height: 50,
        
        padding: 15,
        marginVertical: 10,
        
        borderColor: '#111',
        borderWidth: 1,
        borderRadius: 10,
        
        backgroundColor: '#222',
        color: '#FFF',
        fontSize: 20
    },
    button: {
        backgroundColor: 'green',
        width: '90%',
        height: 50,
        justifyContent: 'center',
        alignItems: 'center',
        marginTop: 10,
        borderRadius: 10
    },
    txtButton: {
        color: '#FFF',
        fontSize: 18,
        fontWeight: 'bold',
    }
});
