import React, { useState } from 'react';
import { Image, TextInput, View, Text, StyleSheet, TouchableOpacity } from 'react-native';
import {auth} from '../../components/config';
import {createUserWithEmailAndPassword,signInWithEmailAndPassword} from 'firebase/auth';
export default function Login({navigation}) {

    const [ email, setEmail ] = useState('');
    const [ password, setPassword ] = useState('');

    async function Login(){
        await signInWithEmailAndPassword(auth,email,password)
        .then(
            ()=>navigation.navigate('Home')
        ).catch(error => alert('Login não efetuado!!'));
    };

 return (
    <View style={styles.view}>

        <View style={styles.containerImg}>

        <Image 
            source={require('../../images/Logo.png')} 
            style={styles.image} 
            resizeMode='contain'
        />

        </View>

        <View style={styles.containerInput}>

        <TextInput 
            value={email}
            onChangeText={setEmail}
            autoCorrect={false}
            style={styles.input}
            placeholder="Digite seu email..."
            placeholderTextColor="#32CD32"
        />
        
        <TextInput 
            value={password}
            secureTextEntry={true}
            autoCorrect={false}
            onChangeText={setPassword}
            style={styles.input}
            placeholderTextColor="#32CD32"
            placeholder="Digite sua senha..."
           // placeholderTextColor="#FFF"
        />

        <TouchableOpacity
            onPress={Login}
            style={styles.button}
        >
            <Text style={styles.txtButton}>Entrar</Text>
        </TouchableOpacity>

        <TouchableOpacity style={{marginBottomWdith:1,marginBottomColor:'#fff',width:80,marginTop:10,borderColor:'#fff',borderBottomWidth:1, justifyContent:'center',alignItems:'center'}} onPress={()=>navigation.navigate('Register')}>
        <Text style={{color:'black', marginTop:5, fontWeight:'bold'}} >Cadastre-se</Text>
        </TouchableOpacity>

        </View>

    </View>
  );
}

const styles = StyleSheet.create({
    view: {
        flex: 1,
        backgroundColor: '#FFFFFF',
        justifyContent: 'center',
        alignItems: 'center'
    },
    containerImg:{
        flex:1,
        justifyContent:'center'
    },
    image: {
        width: 120,
        height: 120,
        
    },
    containerInput:{
        flex:1,
        alignItems:'center',
        justifyContent:'center',
        width:'90%'
    },
    header: {
        fontSize: 34,
        color: 'green',
        fontWeight: 'bold',
        
    },
    input: {
        width: '90%',
        height: 50,
        
        padding: 15,
        marginVertical: 10,
        
        borderColor: '#0C600C',
        borderWidth: 1,
        borderRadius: 10,
        
        backgroundColor: '#000',
        color: '#FFFFFF',
        fontSize: 20
    },
    button: {
        backgroundColor: '#32CD32',
        width: '90%',
        height: 50,
        justifyContent: 'center',
        alignItems: 'center',
        
        borderRadius: 10
    },
    txtButton: {
        color: '#000',
        fontSize: 20,
        fontWeight: 'bold'
    }
})
