import React, { useEffect, useState } from "react";
import {useNavigation} from "@react-navigation/native";
import { View,Text,Image,StyleSheet, ScrollView, SafeAreaView,TouchableOpacity } from "react-native";
import {getProduct} from "../../components/banco";
import { getDocs, collection, deleteDoc, doc } from 'firebase/firestore';





export default function Detalhes ({route}){

    //const [data,setData] = useState({})
    const id = route.params.item;
    const desc = route.params.desc;
    const preco = route.params.preco;
    const titulo = route.params.titulo;
    const img = route.params.img;

    
    
    return(
        
        <SafeAreaView>
        <ScrollView>
            <View style={styles.imageContainer}>
                <Image style={styles.image} source={img} />
            </View>
              <View style={styles.infoContainer} >
                  <Text style={styles.frt}>{tipo}</Text>
                  <View style={{flexDirection:'row',justifyContent:'space-between',alignItems:'center'}}>
                   <Text style={styles.name}>{titulo}</Text>
                  
                  <View style={{flexDirection:'row',alignItems:'center',justifyContent:'space-evenly'}}>
                    <AntDesign
                      name="minuscircleo"
                      size={33}
                      
                      style={{color:'#F3743D',marginLeft: '3%'}}
                      
                    />
                    <Text style={{fontSize: 16,color: '#F3743D',backgroundColor:'#FFF2ED',padding:12,borderRadius:19}}>{count}</Text>
                    <AntDesign
                      name="pluscircleo"
                      size={33}
                      opacity={0.5}
                      style={{color:'black',marginHorizontal: '5%'}}
                      onPress={() => setCount(count + 1)}
                    />
                  </View>
                </View>

                <Text style={styles.price} >{preco} R$</Text>
                <Text style={styles.desc}>Descrição:</Text>
                <Text style={styles.description}>{desc}</Text>
                
                <View style={{flex:1,backgroundColor:'#F3743D',justifyContent:'center',flexDirection:'row',height: 50,alignItems:'center',borderRadius:66}}>
                    <TouchableOpacity style={{flexDirection:'row'}}>
                    <Entypo name="chat"
                     size={29} color="white" 
                     />
                     <Text style={{color:'black',fontWeight:'600' ,marginHorizontal:16,fontSize:13}}>Falar com {'\n'} Vendendor</Text>
                    </TouchableOpacity>
                </View>

                
            </View>
            <Text style={{fontSize:19,fontWeight:'bold',color:'#404040',marginHorizontal:"3%"}}>Outras Frutas </Text>

            <ScrollView horizontal opacity={0.5}>
              <ProductsList> 

              </ProductsList>

            </ScrollView>
            
          
        </ScrollView>
    </SafeAreaView>
  )
}

const styles = StyleSheet.create({
    imageContainer: {
      alignItems: 'center',
      justifyContent: 'center',
      backgroundColor: 'white'
    },
    image: {
      width: '100%',
      aspectRatio: 1,
      borderRadius: 13
    },
    desc : {
      fontSize: 20,
      color:'#0f6e3f',
      fontWeight:'700'

    },
    infoContainer: {
      padding: 16
    },
    name: {
      fontSize: 22,
      fontWeight: '400',
      color:'#F3743D'
    },
    frt: {
      fontSize: 22,
      fontWeight: 'bold',
      fontWeight:'800',
      marginBottom: 8,
    },
    price: {
      fontSize: 23,
      fontWeight: '600',
      marginBottom: 15,
    },
    description: {
      fontSize: 16,
      fontWeight: '400',
      color: '#404040',
      marginBottom: 16,
    },
  });
