import React, { useEffect, useState } from 'react';
import { View, Text, StyleSheet, TouchableOpacity, FlatList } from 'react-native';
import { FontAwesome5, AntDesign } from '@expo/vector-icons';
import { getDocs, collection, deleteDoc, doc } from 'firebase/firestore';
import { db } from '../../components/config';

export default function Home({navigation}) {

    const [ data, setData ] = useState([]);

    useEffect(() => {
        getDocs(collection(db, 'cash')).then(
            (docSnap) => {
                const users = [];
                docSnap.forEach((doc) => {
                    users.push({ 
                        ...doc.data(), 
                        id: doc.id
                    })
                })
                setData(users);
            });
    }, []);

    function deleteData(id) {
        deleteDoc(doc(db, "users", id));
    }

 return (
    <View style={styles.view}>
        <View style={styles.head}>
            <Text style={styles.header}>Meus Gastos</Text>    
        </View>

        <FlatList
            data={data}
            renderItem={
                ({ item }) => (
                    <View style={styles.item}>
                        <View style={styles.data}>
                            <Text style={styles.name}>{item.desc}</Text>
                            <Text style={styles.email}>R$ {item.valor}</Text>
                        </View>

                        <View style={styles.itemButtons}>
                            <TouchableOpacity>
                                <FontAwesome5 name="pencil-alt" size={24} color="black" />
                            </TouchableOpacity>

                            <TouchableOpacity
                                onPress={() => deleteData(item.id)}
                            >
                                <AntDesign name="delete" size={24} color="red" />
                            </TouchableOpacity>
                        </View>
                    </View>
                )}
            keyExtractor={item => item.id}
            showsVerticalScrollIndicator={false}
            contentContainerStyle={{paddingBottom: 50}}
            ListEmptyComponent={() => (
                <Text style={{textAlign: 'center', color: '#fff'}}>
                    Nenhum usu√°rio cadastrado!
                </Text>
            )}
        />

        <TouchableOpacity style={styles.addUser} onPress={() => navigation.navigate('Add')}>
            <Text style={styles.txtAddUser}>+</Text>
        </TouchableOpacity>

        <TouchableOpacity style={styles.addUser2} onPress={() => navigation.navigate('Login')}>
            <Text style={styles.txtAddUser}>Log</Text>
        </TouchableOpacity>
    </View>
  );
}

const styles = StyleSheet.create({
    view: {
        flex: 1,
        paddingTop: 50,
        paddingHorizontal: 25,
        backgroundColor: '#FFF',
        width: '100%',
    },
    image: {
        width: 100,
        height: 100,
        marginBottom: 15,
    },
    head: {
        flexDirection: 'row',
        alignItems: 'center',
        justifyContent: 'center',
        color: '#FFF',
        fontWeight: 'bold',
        marginBottom: 25
    },
    header:{
        color: 'green',
        fontSize: 32,
    },
    input: {
        width: '90%',
        height: 50,

        padding: 15,
        marginVertical: 10,

        borderColor: '#111',
        borderWidth: 1,
        borderRadius: 10,

        backgroundColor: '#222',
        color: '#FFF',
        fontSize: 20
    },
    item: {
        height: 100,
        backgroundColor: '#gray',
        borderWidth:2,
        borderColor:'green',
        elevation: 1,
        marginVertical: 5,
        borderRadius: 10,
        padding: 15,
        flexDirection: 'row',
        alignItems: 'center'
    },
    data: {
        flex: 3,
    },
    itemButtons: {
        flexDirection: 'row',
        paddingHorizontal: 15,
        justifyContent:'space-between',
        flex: 1,
    },
    id: {
        fontSize: 14,
    },
    name: {
        fontSize: 18,
        fontWeight: 'bold',
        color: 'black'
    },
    email: { 
        color: '#000',
        fontSize: 16,
    },
    addUser: {
        position: 'absolute',
        right: 15,
        bottom: 15,
        elevation: 10,
        backgroundColor: 'green',
        borderRadius: 50,
        height: 70,
        width: 70,
        justifyContent: 'center',
        alignItems: 'center'
    },
    addUser2: {
        position: 'relative',
        right: 15,
        bottom: 15,
        elevation: 10,
        backgroundColor: '#19abfd',
        borderRadius: 50,
        height: 70,
        width: 70,
        justifyContent: 'center',
        alignItems: 'center'
    },
    txtAddUser: {
        fontSize: 32,
        fontWeight:'bold',
        color: '#FFF'
    }
})
