import { StatusBar } from 'expo-status-bar';
import React, { useEffect, useState,useLayoutEffect } from "react";
import { useNavigation } from "@react-navigation/native";
import { Dimensions, Button, Alert, SafeAreaView,
   StyleSheet, Text, View, Image, TouchableOpacity,ScrollView,Pressable
} from 'react-native';
///import { getProduct } from "../../components/banco";
import { getDocs, collection, deleteDoc, doc } from 'firebase/firestore';

import { AntDesign } from "@expo/vector-icons";
import { Entypo } from '@expo/vector-icons';
import { Feather } from '@expo/vector-icons';




export default function Detalhes({ route }) {
  const navigation = useNavigation();

  useLayoutEffect(() => {

    navigation.setOptions({
      headerRight: () =>(
  
        <Pressable onPress={alert('clicou')}>
         
          <Entypo 
             name='heart'
             size={30}
             color={"#FF4141"}
             style={{paddingRight:14}}
             />
        </Pressable>
        
      )

    })
 
  },[navigation])

  const id = route.params.item;
  const desc = route.params.desc;
  const preco = route.params.preco;
  const titulo = route.params.titulo;
  const img = route.params.img;
  const idVendedor = route.params.idUser;

  const [count, setCount] = React.useState(1);

  function irCompra(id,titulo,idVendedor,img){
    navigation.navigate("Compra",{id:id,titulo:titulo,idVendedor:idVendedor,img:img})
  }

  return (
    <SafeAreaView>
        <ScrollView>
            <View style={styles.imageContainer}>
                <Image style={styles.image} source={img} />
            </View>
              <View style={styles.infoContainer} >
                  <Text style={styles.frt}>{titulo}</Text>
                  <View style={{flexDirection:'row',justifyContent:'space-between',alignItems:'center'}}>
                   <Text style={styles.name}>{titulo}</Text>
                  
                  <View style={{flexDirection:'row',alignItems:'center',justifyContent:'space-evenly'}}>
                    <AntDesign
                      name="minuscircleo"
                      size={33}
                      
                      style={{color:'#F3743D',marginLeft: '3%'}}
                      onPress={() => setCount(count - 1)}
                    />
                    <Text style={{fontSize: 16,color: '#F3743D',backgroundColor:'#FFF2ED',padding:12,borderRadius:19}}>{count}</Text>
                    <AntDesign
                      name="pluscircleo"
                      size={33}
                      opacity={0.5}
                      style={{color:'black',marginHorizontal: '5%'}}
                      onPress={() => setCount(count + 1)}
                    />
                  </View>
                </View>

                <Text style={styles.price} >{preco} R$</Text>
                <Text style={styles.desc}>Descrição:</Text>
                <Text style={styles.description}>{desc}</Text>

                <TouchableOpacity style={styles.button}>
                  <Text style={styles.buttonText}>Comprar</Text>
                </TouchableOpacity>
                
                

                
            </View>
            <Text style={{fontSize:19,fontWeight:'bold',color:'#404040',marginHorizontal:"3%"}}>Outras Frutas </Text>
            
          
        </ScrollView>
    </SafeAreaView>
  )
}

const styles = StyleSheet.create({
    imageContainer: {
      alignItems: 'center',
      justifyContent: 'center',
      backgroundColor: 'white'
    },
    image: {
      width: '100%',
      aspectRatio: 1,
      borderRadius: 13
    },
    desc : {
      fontSize: 20,
      color:'#0f6e3f',
      fontWeight:'700'

    },
    infoContainer: {
      padding: 16
    },
    name: {
      fontSize: 22,
      fontWeight: '400',
      color:'#0f6e3f'
    },
    frt: {
      fontSize: 22,
      fontWeight: 'bold',
      fontWeight:'800',
      marginBottom: 8,
    },
    price: {
      fontSize: 23,
      fontWeight: '600',
      marginBottom: 15,
    },
    description: {
      fontSize: 16,
      fontWeight: '400',
      color: '#404040',
      marginBottom: 16,
    },
    button: {
      backgroundColor: '#32CD32',
      paddingVertical: 10,
      paddingHorizontal: 20,
      borderRadius: 5,
    },
    buttonText: {
      color: '#FFFFFF',
      fontSize: 18,
      fontWeight: 'bold',
      textAlign: 'center',
    },
  });
