import React, { useState } from 'react';
import { Image, TextInput, View, Text, StyleSheet, TouchableOpacity, Alert } from 'react-native';
import {auth} from '../../components/config';
import {collection, doc, setDoc,addDoc} from 'firebase/firestore';

import {db} from '../../components/config'
import { FirebaseError } from 'firebase/app';
export default function Add({ navigation }) {

    const [ preco, setPreco ] = useState('');
    const [ desc, setDesc ] = useState('');
    const [titulo, setTitulo] = useState('');
   
    
    
    //getDoc para ler os dados

    
    function creator (){

        addDoc(collection(db, "anuncios"), {
            data:'',
            descricao: desc,
            enderreco:'',
            idUser: firebase.auth().currentUser.uid,
            img:'1',
            preco: preco,
            tipoProduto:'legumes',
            titulo:titulo
          }).then(()=>{
            alert("Valor adicionado")

          }).catch((error) => {
            alert("Erro "+error);
          })
          }

 return (
    <View style={styles.view}>
        <Image 
            source={require('../../images/support2.png')} 
            style={styles.image} 
        />
        <Text style={styles.header}>Cadastro de Produto</Text>

        <TextInput 
            value={titulo}
            onChangeText={setTitulo}
            style={styles.input}
            placeholder="Titulo..."
            placeholderTextColor="#FFF"
        />
        <TextInput 
            value={desc}
            onChangeText={setDesc}
            style={styles.input}
            placeholder="descrição do produto..."
            placeholderTextColor="#FFF"
        />

        <TextInput 
            value={preco}
            onChangeText={setPreco}
            style={styles.input}
            placeholder="Valor.."
            placeholderTextColor="#FFF"
        />
        
        <TouchableOpacity onPress={creator}
            style={styles.button}
        >
            <Text style={styles.txtButton}>Adicionar</Text>
        </TouchableOpacity>

    </View>
  );
}

const styles = StyleSheet.create({
    view: {
        flex: 1,
        paddingHorizontal: 25,
        backgroundColor: '#000',
        width: '100%',
        height:'100%',
        justifyContent: 'center',
        alignItems: 'center'
    },
    image: {
        width: 120,
        height: 120,
        marginBottom: 5,
        marginTop:15
    },
    header: {
        fontSize: 34,
        color: 'black',
        fontWeight: 'bold',
        marginBottom:10,
    },
    input: {
        width: '90%',
        height: 50,
        
        padding: 15,
        marginVertical: 10,
        
        borderColor: '#111',
        borderWidth: 1,
        borderRadius: 10,
        
        backgroundColor: '#222',
        color: '#FFF',
        fontSize: 20
    },
    button: {
        backgroundColor: '#82C043',
        width: '90%',
        height: 50,
        justifyContent: 'center',
        alignItems: 'center',
        marginTop: 10,
        borderRadius: 10
    },
    txtButton: {
        color: '#FFF',
        fontSize: 18,
        fontWeight: 'bold',
    }
});
